import React, { useState } from "react";
import Card from "../UI/Card";
import styles from './AddUser.module.css'

function AddUser(){

    const [isTyping, setIsTyping] = useState(false)

    function userTypingHandler(event){

        if(event.target.id === "username" && isTyping === false){
            console.log('username')
            setIsTyping(true)
        };

        if(event.target.id === "age" && isTyping === false){
            console.log('age')
            setIsTyping(true)
        };

        setIsTyping(false)
    }


    function submitHandler(event){
        event.preventDefault();
        console.log("information added");
    }


    return(
        <Card className={styles.input}>
            <form onSubmit={submitHandler}>
                <label htmlFor="username">Username</label>
                <input id="username" type="text" placeholder={!isTyping ? "name" : ""} onInput={userTypingHandler}/>
                <label htmlFor="age">Age(Years)</label>
                <input id="age" type="number" placeholder={!isTyping ? "age" : ""} onInput={userTypingHandler} />
                <button type="submit">Add user!</button>
            </form>
        </Card>
    );
}

export default AddUser;
