import React, { useState } from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import styles from "./AddUser.module.css";

function AddUser(props) {
  const [enteredUsername, setEneteredUsername] = useState("");
  const [enteredAge, setEneteredAge] = useState("");

  function submitHandler(event) {
    event.preventDefault();
    if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
      console.log("Please enter a valid name or age");
      return;
    }
    if (+enteredAge < 1) {
      console.log("Enter an age greater than 1");
      return;
    }
    console.log(enteredAge, enteredUsername);
    setEneteredAge("");
    setEneteredUsername("");

    const newUser = {
        id: Math.random().toString(),
        name: enteredUsername,
        age: enteredAge
    }

    if(enteredUsername !== undefined && enteredAge !== undefined){
        props.onAdd(newUser)
    }
  }

  function usernameChangeHandler(event) {
    setEneteredUsername(event.target.value);
  }

  function ageChangeHandler(event) {
    setEneteredAge(event.target.value);
  }

  return (
    <Card className={styles.input}>
      <form onSubmit={submitHandler}>
        <label htmlFor="username">Username</label>
        <input
          id="username"
          type="text"
          placeholder="name"
          value={enteredUsername}
          onChange={usernameChangeHandler}
        />
        <label htmlFor="age">Age(Years)</label>
        <input
          id="age"
          type="number"
          placeholder="age"
          value={enteredAge}
          onChange={ageChangeHandler}
        />
        <Button type="submit">Add user</Button>
      </form>
    </Card>
  );
}

export default AddUser;
