import React, { useState } from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import styles from "./AddUser.module.css";

function AddUser() {
  const [enteredUsername, setEneteredUsername] = useState("");
  const [enteredAge, setEneteredAge] = useState("");

  function usernameInputHandler(event) {
    if (event.target.id === "username") {
      setEneteredUsername(event.target.value);
      console.log(enteredUsername)
    }

    if (event.target.id === "age") {
      setEneteredAge(event.target.value);
      console.log(enteredAge)
    }
  }

  function submitHandler(event) {
    event.preventDefault();
    console.log("information added");
  }

  return (
    <Card className={styles.input}>
      <form onSubmit={submitHandler}>
        <label htmlFor="username">Username</label>
        <input
          id="username"
          type="text"
          placeholder="name"
          onInput={usernameInputHandler}
        />
        <label htmlFor="age">Age(Years)</label>
        <input
          id="age"
          type="number"
          placeholder="age"
          onInput={usernameInputHandler}
        />
        <Button type="submit">Add user</Button>
      </form>
    </Card>
  );
}

export default AddUser;
